package com.protocol7.quincy.tls.messages;

import static com.protocol7.quincy.tls.TestUtil.assertHex;
import static org.junit.Assert.assertEquals;

import com.protocol7.quincy.tls.extensions.SupportedVersions;
import com.protocol7.quincy.tls.extensions.TransportParameters;
import com.protocol7.quincy.tls.messages.ServerHandshake.EncryptedExtensions;
import com.protocol7.quincy.tls.messages.ServerHandshake.ServerCertificate;
import com.protocol7.quincy.tls.messages.ServerHandshake.ServerCertificateVerify;
import com.protocol7.quincy.tls.messages.ServerHandshake.ServerHandshakeFinished;
import com.protocol7.quincy.utils.Hex;
import io.netty.buffer.ByteBuf;
import io.netty.buffer.Unpooled;
import org.junit.Test;

public class ServerHandshakeTest {

  @Test
  public void parseKnownServerCertificate() {
    final ByteBuf bb =
        Unpooled.wrappedBuffer(
            Hex.dehex(
                ""));

    final ServerCertificate sc = ServerCertificate.parse(bb);

    assertEquals(2, sc.getServerCertificates().size());
    assertHex(
        "",
        sc.getServerCertificates().get(0));
    assertHex(
        "",
        sc.getServerCertificates().get(1));
  }

  @Test
  public void parseKnownServerCertificateVerify() {
    final ByteBuf bb =
        Unpooled.wrappedBuffer(
            Hex.dehex(
                ""));

    final ServerCertificateVerify scv = ServerCertificateVerify.parse(bb);
    assertEquals(2052, scv.getType());
    assertHex(
        "",
        scv.getSignature());
  }

  @Test
  public void parseKnownServerHandshakeFinished() {
    final ByteBuf bb =
        Unpooled.wrappedBuffer(
            Hex.dehex("140000200c3dcdc53b56e8d4a127d104737ffc3093d005c7134837958cf32c33ee57ea96"));

    final ServerHandshakeFinished fin = ServerHandshakeFinished.parse(bb);

    assertHex(
        "0c3dcdc53b56e8d4a127d104737ffc3093d005c7134837958cf32c33ee57ea96",
        fin.getVerificationData());
  }

  @Test
  public void roundtrip() {
    final byte[] cert =
        Hex.dehex(
            "");
    final byte[] certVerificationSig =
        Hex.dehex(
            "");
    final byte[] certVerificationData =
        Hex.dehex("4c6e3380e3b4034484753f79b0946ffc8a201fb4d3c1e8031a815ede45d9dbed");

    final EncryptedExtensions ee = new EncryptedExtensions(SupportedVersions.TLS13);
    final ServerCertificate sc = new ServerCertificate(cert);
    final ServerCertificateVerify scv = new ServerCertificateVerify(2052, certVerificationSig);
    final ServerHandshakeFinished fin = new ServerHandshakeFinished(certVerificationData);

    final ByteBuf bb = Unpooled.buffer();
    ee.write(bb);
    sc.write(bb);
    scv.write(bb);
    fin.write(bb);

    final ServerHandshake parsed = ServerHandshake.parse(bb, true);

    assertEquals(ee.getExtensions(), parsed.getEncryptedExtensions().getExtensions());
    assertEquals(
        sc.getServerCertificates().size(),
        parsed.getServerCertificate().getServerCertificates().size());
    assertHex(
        sc.getServerCertificates().get(0),
        parsed.getServerCertificate().getServerCertificates().get(0));
    assertEquals(scv.getType(), parsed.getServerCertificateVerify().getType());

    assertHex(scv.getSignature(), parsed.getServerCertificateVerify().getSignature());
    assertHex(fin.getVerificationData(), parsed.getServerHandshakeFinished().getVerificationData());
  }
}
